var isMobile = /iPhone|Android/i.test(navigator.userAgent);
mobile=''
if (/iPhone/i.test(navigator.userAgent)){mobile = ': iPhone'}
if (/iPhone/i.test(navigator.userAgent)){mobile = ': Android'}
var head = document.getElementsByTagName('HEAD')[0];
var link = document.createElement('link');
if (isMobile) {
    alert('For user experience of mobile' + mobile + ' users, I show my website in landscape view. ')
    link.rel = 'stylesheet';
    link.type = 'text/css';
    link.href = 'mobile.css';
    head.appendChild(link);
} else {
    link.rel = 'stylesheet';
    link.type = 'text/css';
    link.href = 'desktop.css';
    head.appendChild(link);
}












