$(document).ready(function(){
	var isMobile = /iPhone|Android/i.test(navigator.userAgent);
	mobile=''
	if (/iPhone/i.test(navigator.userAgent)){mobile = 'iPhone'}
	if (/Android/i.test(navigator.userAgent)){mobile = 'Android'}
	var head = document.getElementsByTagName('HEAD')[0];
	var link = document.createElement('link');
	var w = window.innerWidth * 0.95;
	var h = window.innerHeight * 0.95;



	if (isMobile) {
		if (document.title =='Chong Shu') {
			if (sessionStorage.getItem('alerted') != 'yes') {
			   alert('For user experience of ' + mobile + ' users, I display my website in landscape mode. ')
			   sessionStorage.setItem('alerted', 'yes')
			}
		}
		link.rel = 'stylesheet';
		link.type = 'text/css';
		link.href = 'mobile.css';
		head.appendChild(link);
		// scale = Math.min(w/1200, h/650)
		// scale = 1.2
		// $('#wrap').css({'-webkit-transform': 'scale(' + scale + ')'});
	} else {
		link.rel = 'stylesheet';
		link.type = 'text/css';
		link.href = 'desktop.css';
		head.appendChild(link);
	}

}); 















